import React, { useContext, useEffect } from "react"
import { UserContext } from "../context/UserProvider.js"
import IssueCard from "./IssueCard.js"

const style = {
  container: `flex flex-col items-center h-screen`,
  header: `text-4xl text-center m-5`,
}

const Public = (props) => {
  const {
    getAllIssues,
    allIssues,
    deleteIssue,
    user: { username },
    issues,
  } = useContext(UserContext)
  const publicIssues = allIssues.map((issue) => (
    <IssueCard {...issue} key={issue._id} />
  ))
  const { downvotesCount, upvotesCount } = props

  useEffect(() => {
    console.log("use effect ran")
    getAllIssues()
  }, [downvotesCount + upvotesCount, issues, downvotesCount, upvotesCount])

  return (
    <div className={style.container}>
      <h1 className={style.header}>All Users' Issues</h1>
      <h2 style={userHeadingStyle}>
        You are currently logged in as user: {username}
      </h2>
      <div style={publicIssueStyle}>{publicIssues}</div>
    </div>
  )
}

export default Public

const publicIssueStyle = {
  alignItems: "center",
  display: "flex",
  flexDirection: "column",
}

const userHeadingStyle = {
  textAlign: "center",
  fontSize: "18px",
}
