import React from "react"

const style = {
  form: `flex flex-col p-5 rounded-md bg-[#aaa9] border-2 border-[#3339]`,
  input: `my-4 mx-2 rounded`,
  button: `my-4 mx-2 rounded`,
}
const AuthForm = (props) => {
  const {
    handleChange,
    handleSubmit,
    btnText,
    errMsg,
    inputs: { username, password },
  } = props

  return (
    <section className='bg-gray-50'>
      <div className='flex flex-col items-center justify-center px-6 py-8 max-auto md:h-screen lg:py-0'>
        <div className='p-6 space-y-4 md:space-y-6 sm:p-8'>
          <h1>Log in</h1>
          <form onSubmit={handleSubmit} className={style.form}>
            <input
              type='text'
              value={username}
              name='username'
              onChange={handleChange}
              placeholder='Username'
              className={style.input}
            />
            <input
              type='text'
              value={password}
              name='password'
              onChange={handleChange}
              placeholder='Password'
              className={style.input}
            />
            <button className={style.button}>{btnText}</button>
            <p style={{ color: "red" }}>{errMsg}</p>
          </form>
        </div>
      </div>
    </section>
  )
}

export default AuthForm
